// const geojson = {
//     type: 'FeatureCollection',
//     features: [
//       {type: 'Feature', geometry: {type: 'Point', coordinates: [-122.4, 37.8]}}
//     ]
//   };

  makeCircle(county, latitude, longitude, cases) {
      let source = this.makeSource(county, latitude, longitude);
      let layer = this.makeLayer(county, cases);
      source.props.children.push(layer);
      return source;
  }

  makeSource(county, latitude, longitude) {
    let source_properties = {
        id: county + "_source", 
        type: 'geojson', 
        data: {
            type: 'FeatureCollection', 
            features: [
                {
                    type: 'Feature', 
                    geometry: {
                        type: 'Point', 
                        coordinates: [latitude, longitude]
                    }
                }
            ]
        }
    }
    return (
        <Source {...source_properties} />
    )
  }

  makeLayer(county, cases) {
    let layer_properties = {
        id: county + "_layer", 
        type: "circle", 
        paint: {
            'circle-radius': 10, 
            'circle-color': '#0282828'
        }
    }

    return (
        <Layer {...layer_properties} />
    )
  }



  updateCasesLayer() {
      axios.get("https://us-central1-iris-263608.cloudfunctions.net/close_ca_map_all").then(
          (response) => {
            let build_cases_layer = []
            build_cases_layer.push(
                makeCircle("LA", 40.721086, -120.629952, 123)
            )
            this.setState({
                cases_layer: build_cases_layer
            })
          }
      ).catch(
          (err) => {
              console.log(err);
          }
      )
  }

  render() {

    let mapbox_token = "pk.eyJ1Ijoic2hhbGludnMiLCJhIjoiY2s4ZnNtZHhlMDd0NzNrcGU4eHJnYXgyOCJ9.qv2ft9xqJ32Ovea3vDq3Yg";
    let viewport = this.getViewport();
    return (
        <MapGL 
            {...viewport} 
            mapboxApiAccessToken={mapbox_token} 
            onViewportChange={(viewport) => { this.setViewport(viewport)}}
        >
        </MapGL>
    );
  }

}







export default Map;
