                      <CountyInfo county={this.state.county} />
                    </div>
                    <div style={{visibility: "hidden"}} className="Map">
                      <Map county={this.state.county} mapOnClick={this.mapOnClick}/>
                    </div>
                    <MobileNav changeActiveMobileComponent={this.changeActiveMobileComponent} />
                  </div>

                ) 

                : 

                (
                  <div className="App-container">
                    <div className="County-info">
                      <CountyInfo county={this.state.county} />
                    </div>
                    <div style={{display: "none"}} className="Map">
                      <Map county={this.state.county} mapOnClick={this.mapOnClick}/>
                    </div>
                  </div>

                ) 
            }
          }
        </MediaQuery>

 */}

        <MobileMenu prev_active_mobile_component={this.state.active_mobile_component} selected_county={this.state.county}/>

        <div className="App-container">
          <div className="County-info">
            <CountyInfo county={this.state.county} />
          </div>
          <div className="Map">
              <Map selected_county={this.state.county} mapOnClick={this.mapOnClick}/>
          </div>
        </div>


        <MobileNav changeActiveMobileComponent={this.changeActiveMobileComponent} />

      </div>
    );
  }

}







export default App;
