import React, {Component} from 'react'; 
// import '../components/addOpp.css';
import 'react-phone-number-input/style.css'
import PhoneInput from 'react-phone-number-input'

//refactor into a controlled component 
//on click, go grey
//or on click, disappear
//on hover for add, go blue
//Make long for add button

class AddPhone extends Component{

    constructor(props){
        super(props);
            this.state = {
                phone: "",
                copied: false
            };
        }
    // changeHandler = e => {
    //     this.setState({phone: e.target.value});
    // }

    submitHandler = e => {
        e.preventDefault()
        axios.post(`/phone`, {
            comment: this.state.comment,
            mention_id: this.state.mentionedUserId
          })

          .then((response)=>{
            console.log(response);
              this.setState({onError: false});
              //call third party update
              // this.props.refresh();

         })

          .catch((error)=>{
            console.log(error);
              this.setState({onError: true});
         });
        // console.log("should input test data" +this.state.phone)
        // fetch('/api/phone', {
        //     method: 'POST',
        //     headers: {
        //         'Accept': 'application/json',
        //         'Content-Type': 'application/json',
        //     },
        //     body: JSON.stringify({
        //         "phone": this.state.phone
        //     })
        //     })
        //     this.setState({copied: true});
    }

    render() {
        return (
            <div className = "FormContainer"> 
            <div className = "OppText">Enter your phone number </div>
            <form onSubmit= {this.submitHandler} className = "Form">
                {/* <input className = "FormInput" type = "text" placeholder = "Your Phone Number" value ={this.state.name} onChange = {this.changeHandler}>
                </input> */}
                <PhoneInput
                    defaultCountry={'US'}
                    placeholder = "Enter your phone number"
                    value={this.state.phone}
                    onChange={phone => this.setState({ phone })}
                />
                <button type = "submit" className = "FormButton" > Add
                </button>
            </form>
            {/* {this.state.copied ? 
                    <React.Fragment>
                     <ConfirmButton
                     text = "Sent to Peerlift!"
                     visible = {false}
                     ></ConfirmButton>
                      </React.Fragment>
                     : null} */}
            </div>
        );
    }
  }

  export default AddPhone;
