import React from 'react';
import MapGL, {Source, Layer, Popup} from 'react-map-gl';
import axios from 'axios';
class Map extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
        clicked_county: null,
        latitude: 40.721086,
        longitude: -120.629952,
        width: "10vw",
        height: "100%",
        zoom: 10,
        cases_layer: null, 
        interactiveLayerIds: null
    };

  }

  componentDidMount() {
    console.log("ddd")
    this.setState({
        latitude: 40.721086,
        longitude: -120.629952,
        width: "100vw",
        height: "100vh",
        zoom: 10,
    });
    this.updateCasesLayer();
  }



  setViewport(viewport) {
    this.setState({
        latitude: viewport.latitude, 
        longitude: viewport.longitude, 
        width: viewport.width,
        height: viewport.height,
        zoom: viewport.zoom
    })
  }

  getViewport() {
    return {
        latitude: this.state.latitude, 
        longitude: this.state.longitude, 
        width: this.state.width,
        height: this.state.height,
        zoom: this.state.zoom
    }
  }


//   map.addSource('point', {
//     'type': 'geojson',
//     'data': pointOnCircle(0)
//     });

//     map.addLayer({
//     'id': 'point',
//     'source': 'point',
//     'type': 'circle',
//     'paint': {
//     'circle-radius': 10,
//     'circle-color': '#007cbf'
//     }
//     });

// <Source id="my-data" type="geojson" data={geojson}>
//           <Layer
//             id="point"
//             type="circle"
//             paint={{
//               'circle-radius': 10,
//               'circle-color': '#007cbf'
//             }} />
//         </Source>

// const geojson = {
//     type: 'FeatureCollection',
//     features: [
//       {type: 'Feature', geometry: {type: 'Point', coordinates: [-122.4, 37.8]}}
//     ]
//   };



  makeSource(county, latitude, longitude, cases) {
    let source_properties = {
        id: county, 
        type: 'geojson', 
        data: {
            type: 'FeatureCollection', 
            features: [
                {
                    type: 'Feature', 
                    geometry: {
                        type: 'Point', 
                        coordinates: [longitude, latitude]
                    }
                }
            ]
        }
    }
    return (
        <Source key={county} {...source_properties}>
            {this.makeLayer(county, cases)}
        </Source>
    )
  }

  makeLayer(county, cases) {

    let layer_properties = {
        id: county, 
        type: "circle", 
        paint: {
            'circle-radius': cases/75, 
            'circle-color': '#ffa700', 
            'circle-opacity': 0.3, 
            'circle-stroke-width': 1, 
            'circle-stroke-color': '#ffa700'

        }
    }

    return (
            <Layer {...layer_properties}>
            </Layer>

    )
  }



  updateCasesLayer() {
      axios.get("https://us-central1-iris-263608.cloudfunctions.net/close_ca_map_all").then(
          (response) => {
            let build_cases_layer = [];
            let counties = [];
            for(let i = 0; i < response.data.items.length; ++i) {
                let county_info = response.data.items[i];
                let circle = this.makeSource(county_info.county, county_info.lat, county_info.lon, county_info.cases);
                build_cases_layer.push(circle);
                counties.push(county_info.county);

            }

            this.setState({
                cases_layer: build_cases_layer,
                interactiveLayerIds: counties
            })
          }
      ).catch(
          (err) => {
              console.log(err);
          }
      )
  }

  onClickCounty(event) {
    let feature = event.features[0];
    if(feature.source === undefined) return;
    let selected_county = feature.source;

    this.props.mapOnClick(selected_county);
  }

  render() {

    let mapbox_token = "pk.eyJ1Ijoic2hhbGludnMiLCJhIjoiY2s4ZnNtZHhlMDd0NzNrcGU4eHJnYXgyOCJ9.qv2ft9xqJ32Ovea3vDq3Yg";
    let viewport = this.getViewport();
    return (
        <MapGL 
            {...viewport} 
            mapboxApiAccessToken={mapbox_token} 
            onViewportChange={(viewport) => { this.setViewport(viewport)}}
            interactiveLayerIds={this.state.interactiveLayerIds}
            onClick={(event) => {this.onClickCounty(event)}}
            onMouseEnter={(event) => {console.log("hello")}}
        >
            {this.state.cases_layer}
        </MapGL>
    );
  }

}







export default Map;
