import React, {Component} from 'react'; 
import axios from 'axios';



class MobileMenu extends Component{
    constructor(props) {
        super(props);
        //TODO: get prop prev component 
        //TODO: get prop current county
        this.state = {
            selected_county: null, 
            counties: null
        }

    }

    componentDidMount() {
        axios.get("https://us-central1-iris-263608.cloudfunctions.net/close_ca_map_all").then(
            (response) => {
                let build_counties = [];
                for(let i = 0; i < response.data.items.length; ++i) {
                    let county_info = response.data.items[i];
                    build_counties.push(county_info.county);
                }
                this.setState({
                    counties: this.makeCountiesList(build_counties)
                })
            }
        ).catch(
            (err) => {
                if(err) {
                    console.log(err);
                }
            }
        );
    }

    makeCountiesList(counties) {
        let build_list = [];
        for(let i = 0; i < counties.length; ++i) {
            let county = counties[i];
            build_list.push(
                <div className="List-element">
                     <ion-icon 
                        style={{color: this.getIconColor(county)}} 
                        name="stop"
                        ></ion-icon> {county}
                </div>
            )
        }
        return build_list;
    }

    getIconColor(county) {
        if(this.state.selected_county === county) {
            return "#ffa700";
        }else {
            return "#27292d";
        }
    }

    render() {
        return (
            <div>
                {this.state.counties}
            </div>

        );
    }
  }

  export default MobileMenu
