import React from 'react';
import "./countyInfo.css";
import axios from 'axios';





class CountyInfo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentCounty: null, 
      currentNumCases: null, 
      currentNumDeceased: null,
      newCasesToday: null, 
      newDeathsToday: null, 
      newsFeed: []
    }
  }

  componentDidMount() {
    /**
     * Replace this with a GET request once endpoint is written. 
     */

    var testState = {
      currentCounty: "Los Angelas County", 
      currentNumCases: 0, 
      currentNumDeceased: 0,
      newCasesToday: 0, 
      newDeathsToday: 0, 
      newsFeed: [
        {
          title: "Temporary shelters open across LA.", 
          sourcename: "Los Angelas Times", 
          sourceurl: "google.com",
          timestamp: "2 min ago"
        }, 

        {
          title: "Some other news article", 
          sourcename: "Los Angelas Times", 
          sourceurl: "https://www.google.com",
          timestamp: "5 min ago"
        }
      ]
    }

    this.setState(testState)

  }

  displayNewsFeed() {
    return this.state.newsFeed.sort( //sort the newsfeed articles by date, earliest to latest
      function(article1, article2) {
        return new Date(article1.timestamp) - new Date(article2.timestamp);
      }
    ).map(
      function(article) {
        return (
          <div>
            <div>
              {article.timestamp}
            </div>
            <div>
              <a href={article.sourceurl}>
                {article.title}
              </a>
            </div>
            <div>
              {article.sourcename}
            </div>

          </div>
        )
      }
    )
  }


  render() {
    /**
     * 
     */
    return (
      <div className="County-info">
          <div className="Statistics-view">
            <div className="Displaying">
              Displaying 
              <div className="County">
                {this.state.currentCounty}
              </div>
            </div>

            <div className="COVID-19-changes-in-your-area">
              COVID-19 changes in your area



            </div>


            <div className="Showing-Department-of-Public-Health-data">
              Showing Department of Public Health data
            </div>

          </div>



          <div className="News-feed">

            Local updates for Los Angelas & Orange County:
            {this.displayNewsFeed()}
          </div>
      </div>
    )

  }
}



export default CountyInfo;
